package expo.modules.callstatusimport android.telephony.TelephonyCallback;import android.telephony.TelephonyManager;class TelephonyCallbackListener: TelephonyCallback(), TelephonyCallback.CallStateListener {    private var callState: String = "IDLE";    override fun onCallStateChanged(state: Int) {        callState = when (state) {            TelephonyManager.CALL_STATE_IDLE -> "IDLE"            else -> "IN CALL"        }    }    fun getCallState(): String {        return callState;    }}